import React from 'react';

const Skills = () => {
  // Organized skill groups focused on Cloud/DevOps
  const skillGroups = [
    {
      title: 'Cloud & DevOps',
      icon: '‚òÅÔ∏è',
      skills: ['AWS', 'Docker', 'GitHub Actions', 'Linux', 'Bash', 'Terraform']
    },
    {
      title: 'Languages',
      icon: 'üíª',
      skills: ['Python', 'JavaScript', 'TypeScript', 'SQL']
    },
    {
      title: 'Tools',
      icon: 'üõ†Ô∏è',
      skills: ['Postman', 'APIs (REST & GraphQL)', 'Jira', 'Trello']
    },
    {
      title: 'Frameworks/Libraries',
      icon: 'üìö',
      skills: ['React.js', 'Node.js', 'pandas', 'NumPy', 'scikit-learn', 'PostgreSQL', 'MongoDB']
    }
  ];

  return (
    <section id="skills" className="py-20 bg-white">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Section header */}
        <div className="text-center mb-16">
          <h2 className="text-3xl sm:text-4xl font-bold text-gray-900 mb-4">
            Technical Skills
          </h2>
          <p className="text-lg text-gray-600 max-w-2xl mx-auto">
            Core technologies and tools I use to build scalable cloud infrastructure, 
            automate deployments, and manage modern DevOps pipelines.
          </p>
        </div>

        {/* Skills grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
          {skillGroups.map((group, groupIndex) => (
            <div
              key={group.title}
              className="bg-gray-50 rounded-xl p-6 hover:shadow-lg transition-shadow duration-300"
            >
              {/* Group header */}
              <div className="flex items-center mb-6">
                <span className="text-2xl mr-3">{group.icon}</span>
                <h3 className="text-xl font-semibold text-gray-900">
                  {group.title}
                </h3>
              </div>

              {/* Skills list */}
              <div className="space-y-3">
                {group.skills.map((skill, skillIndex) => (
                  <div
                    key={skill}
                    className="flex items-center p-3 bg-white rounded-lg shadow-sm hover:shadow-md transition-shadow duration-200"
                    style={{
                      animationDelay: `${(groupIndex * 3 + skillIndex) * 0.1}s`
                    }}
                  >
                    {/* Skill indicator dot */}
                    <div className="w-3 h-3 bg-primary-500 rounded-full mr-3 flex-shrink-0"></div>

                    {/* Skill name */}
                    <span className="text-gray-700 font-medium">{skill}</span>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>

        {/* Additional info section */}
        <div className="mt-16 text-center">
          <div className="bg-gradient-to-r from-primary-50 to-blue-50 rounded-xl p-8">
            <h3 className="text-xl font-semibold text-gray-900 mb-4">
              Always Learning
            </h3>
            <p className="text-gray-600 max-w-3xl mx-auto">
              Cloud technology evolves rapidly, and I'm committed to continuous learning. 
              Currently pursuing AWS certifications and exploring advanced DevOps practices like 
              Infrastructure as Code, CI/CD optimization, and cloud-native architectures.
            </p>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Skills;
