import React, { useState, useEffect, useRef } from 'react';

function App() {
  const [currentSection, setCurrentSection] = useState('about');
  const [isLoading, setIsLoading] = useState(true);
  const [bootSequence, setBootSequence] = useState([]);
  const mainRef = useRef(null);

  const sections = {
    about: 'About',
    experience: 'Experience', 
    education: 'Education',
    skills: 'Skills',
    projects: 'Projects',
    certifications: 'Certifications'
  };

  const sectionKeys = Object.keys(sections);

  const bootMessages = [
    'Loading system modules...',
    'Initializing portfolio interface...',
    'Mounting file systems...',
    'Starting network services...',
    'Loading user profile...',
    'Ready.'
  ];

  useEffect(() => {
    let messageIndex = 0;
    const bootInterval = setInterval(() => {
      if (messageIndex < bootMessages.length) {
        setBootSequence(prev => [...prev, bootMessages[messageIndex]]);
        messageIndex++;
      } else {
        clearInterval(bootInterval);
        setTimeout(() => setIsLoading(false), 500);
      }
    }, 300);

    return () => clearInterval(bootInterval);
  }, []);

  useEffect(() => {
    const handleKeyDown = (e) => {
      if (isLoading) return;

      const currentIndex = sectionKeys.indexOf(currentSection);

      if (e.key === 'ArrowLeft' || e.key === 'ArrowUp') {
        e.preventDefault();
        const prevIndex = currentIndex > 0 ? currentIndex - 1 : sectionKeys.length - 1;
        setCurrentSection(sectionKeys[prevIndex]);
      } else if (e.key === 'ArrowRight' || e.key === 'ArrowDown') {
        e.preventDefault();
        const nextIndex = currentIndex < sectionKeys.length - 1 ? currentIndex + 1 : 0;
        setCurrentSection(sectionKeys[nextIndex]);
      } else if (e.key >= '1' && e.key <= '6') {
        e.preventDefault();
        const index = parseInt(e.key) - 1;
        if (index < sectionKeys.length) {
          setCurrentSection(sectionKeys[index]);
        }
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [currentSection, isLoading, sectionKeys]);

  const renderPrompt = () => (
    <div className="text-green-400 mb-2">
      <span className="text-blue-400">jon@portfolio</span>
      <span className="text-white">:</span>
      <span className="text-purple-400">~/{currentSection}</span>
      <span className="text-white">$ </span>
    </div>
  );

  const renderAbout = () => (
    <div className="space-y-4">
      {renderPrompt()}
      <div className="text-gray-300 leading-relaxed">
        <p className="text-yellow-400 mb-2">// Final year Computer Engineering student</p>
        <p>Hands-on experience in Linux, AWS, Docker, Python and CI/CD using GitHub Actions.</p>
        <p>Self-driven, solutions-oriented and committed to continuous improvement.</p>
        <p>Bringing analytical thinking, product vision and clear communication.</p>
        <p className="text-green-400 mt-3">Languages: Spanish (native), English (C1), German (A2)</p>
      </div>
    </div>
  );

  const renderEducation = () => (
    <div className="space-y-4">
      {renderPrompt()}
      <div className="text-gray-300">
        <div className="bg-gray-800 p-4 rounded border-l-4 border-blue-400">
          <div className="flex justify-between items-start mb-2">
            <h3 className="text-white font-medium">Universitat Oberta de Catalunya</h3>
            <span className="text-green-400 text-sm">Jul 2026</span>
          </div>
          <p className="text-yellow-400 mb-2">Bachelor in Computer Science - Computer Engineering</p>
          <p className="text-sm mb-2">Online</p>
          <div className="text-sm">
            <p className="text-blue-400 mb-1">Highlighted courses:</p>
            <ul className="list-disc list-inside text-gray-400 space-y-1">
              <li>Network and Operating System Administration (9.0)</li>
              <li>Internet Networks and Applications (9.7)</li>
              <li>Computer Architecture (9.3)</li>
              <li>Graph Theory and Complexity (9.7)</li>
              <li>Artificial Intelligence (9.0)</li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );

  const renderExperience = () => (
    <div className="space-y-4">
      {renderPrompt()}
      <div className="text-gray-300 space-y-6">
        <div className="bg-gray-800 p-4 rounded border-l-4 border-green-400">
          <div className="flex justify-between items-start mb-2">
            <h3 className="text-white font-medium">Default Club (E-commerce)</h3>
            <span className="text-green-400 text-sm">Madrid, Spain</span>
          </div>
          <div className="flex justify-between items-center mb-3">
            <span className="text-yellow-400">Cloud Automation Engineer</span>
            <span className="text-gray-400 text-sm">Feb 2023 – Jul 2025</span>
          </div>
          <ul className="list-disc list-inside text-sm space-y-1 text-gray-400">
            <li>Automated product catalogue, saving 20h/week via REST/GraphQL APIs</li>
            <li>Introduced CI/CD with GitHub Actions and Docker on AWS EC2</li>
            <li>Deployed monitoring with Prometheus, KPIs and Discord alerts</li>
            <li>Web scraping and market analysis with Python (BeautifulSoup, Pandas)</li>
            <li>Built cloud pipelines with AWS S3 and Lambda for serverless operations</li>
          </ul>
        </div>

        <div className="bg-gray-800 p-4 rounded border-l-4 border-purple-400">
          <div className="flex justify-between items-start mb-2">
            <h3 className="text-white font-medium">Default Club</h3>
            <span className="text-green-400 text-sm">Madrid, Spain</span>
          </div>
          <div className="flex justify-between items-center mb-3">
            <span className="text-yellow-400">Founder</span>
            <span className="text-gray-400 text-sm">Jul 2017 – Jul 2025</span>
          </div>
          <p className="text-sm text-gray-400">Built and scaled e-commerce brand from scratch, serving 25,000+ customers. Led technical automation of core operations focusing on performance, scalability, and low operational overhead.</p>
        </div>
      </div>
    </div>
  );

  const renderSkills = () => (
    <div className="space-y-4">
      {renderPrompt()}
      <div className="text-gray-300 space-y-4">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div className="bg-gray-800 p-4 rounded">
            <h4 className="text-blue-400 font-medium mb-2">Programming</h4>
            <div className="flex flex-wrap gap-2">
              {['Python', 'JavaScript', 'React', 'Node.js', 'HTML', 'SQL', 'Bash'].map(skill => (
                <span key={skill} className="bg-gray-700 px-2 py-1 rounded text-sm text-green-400">{skill}</span>
              ))}
            </div>
          </div>

          <div className="bg-gray-800 p-4 rounded">
            <h4 className="text-blue-400 font-medium mb-2">DevOps & Cloud</h4>
            <div className="flex flex-wrap gap-2">
              {['Docker', 'Kubernetes', 'Terraform', 'Git', 'AWS', 'PostgreSQL', 'Prometheus'].map(skill => (
                <span key={skill} className="bg-gray-700 px-2 py-1 rounded text-sm text-yellow-400">{skill}</span>
              ))}
            </div>
          </div>

          <div className="bg-gray-800 p-4 rounded md:col-span-2">
            <h4 className="text-blue-400 font-medium mb-2">Systems</h4>
            <div className="flex flex-wrap gap-2">
              {['Linux Administration', 'CLI Tools', 'bash', 'ssh', 'systemctl', 'journalctl', 'curl'].map(skill => (
                <span key={skill} className="bg-gray-700 px-2 py-1 rounded text-sm text-purple-400">{skill}</span>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );

  const renderProjects = () => (
    <div className="space-y-4">
      {renderPrompt()}
      <div className="text-gray-300">
        <div className="bg-gray-800 p-4 rounded border-l-4 border-yellow-400">
          <h3 className="text-white font-medium mb-2">Fbref Scraper</h3>
          <p className="text-sm text-gray-400 mb-3">Docker, Python, Requests, BeautifulSoup, Pandas, GitHub</p>
          <p className="text-sm">Developed Python scraper collecting 30,000+ advanced football statistics from FBref, exporting data to CSV for external analysis.</p>
        </div>
      </div>
    </div>
  );

  const renderCertifications = () => (
    <div className="space-y-4">
      {renderPrompt()}
      <div className="text-gray-300 space-y-4">
        <div className="bg-gray-800 p-4 rounded border-l-4 border-cyan-400">
          <div className="flex justify-between items-start mb-2">
            <h3 className="text-white font-medium">DevOps with Docker & Kubernetes</h3>
            <span className="text-yellow-400 text-sm">In progress</span>
          </div>
          <p className="text-sm text-gray-400 mb-1">Universidad de Helsinki (240h) - Online</p>
        </div>

        <div className="bg-gray-800 p-4 rounded border-l-4 border-green-400">
          <div className="flex justify-between items-start mb-2">
            <h3 className="text-white font-medium">Google Professional Certificate in Business Intelligence</h3>
            <span className="text-green-400 text-sm">2025</span>
          </div>
          <p className="text-sm text-gray-400">Google (80h) - Online</p>
        </div>
      </div>
    </div>
  );

  const renderContent = () => {
    switch(currentSection) {
      case 'about': return renderAbout();
      case 'education': return renderEducation();
      case 'experience': return renderExperience();
      case 'skills': return renderSkills();
      case 'projects': return renderProjects();
      case 'certifications': return renderCertifications();
      default: return renderAbout();
    }
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-black text-green-400 font-mono p-8">
        <div className="max-w-4xl mx-auto">
          <div className="mb-4">
            <span className="text-cyan-400">jon@portfolio</span>
            <span className="text-white">:~$ </span>
            <span className="animate-pulse">portfolio --init</span>
          </div>
          {bootSequence.map((message, index) => (
            <div key={index} className="text-sm opacity-80 mb-1 boot-message">
              <span className="text-green-400">[{String(index + 1).padStart(2, '0')}]</span> {message}
            </div>
          ))}
          <div className="terminal-cursor text-green-400"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-black text-green-400 font-mono">
      <div className="max-w-6xl mx-auto p-8">
        <header className="mb-8">
          <div className="text-center mb-6">
            <h1 className="text-2xl text-cyan-400 mb-2">Jon Hidalgo</h1>
            <p className="text-gray-400">Computer Engineering Student | DevOps & Cloud Automation</p>
          </div>

          <nav className="bg-gray-900 p-4 rounded border border-gray-700 terminal-border" role="tablist">
            <div className="flex flex-wrap gap-4 justify-center">
              {Object.entries(sections).map(([key, label], index) => (
                <button
                  key={key}
                  onClick={() => setCurrentSection(key)}
                  className={`px-3 py-1 rounded transition-colors ${
                    currentSection === key 
                      ? 'bg-blue-600 text-white terminal-glow' 
                      : 'text-gray-400 hover:text-white hover:bg-gray-700'
                  }`}
                  aria-label={`Navigate to ${label} section. Press ${index + 1} or use arrow keys`}
                  role="tab"
                  aria-selected={currentSection === key}
                  tabIndex={currentSection === key ? 0 : -1}
                >
                  <span className="text-xs text-gray-500 mr-1">{index + 1}</span>
                  {label}
                </button>
              ))}
            </div>
            <div className="text-xs text-gray-500 text-center mt-2">
              <span className="sr-only">Navigation instructions: </span>
              Use arrow keys or numbers 1-6 to navigate
            </div>
          </nav>
        </header>

        <main 
          ref={mainRef}
          className="bg-gray-900 rounded border border-gray-700 p-6 min-h-96 terminal-border"
          role="tabpanel"
          aria-labelledby="current-section"
          tabIndex="0"
        >
          <div id="current-section" className="sr-only">
            {sections[currentSection]} section content
          </div>
          {renderContent()}
        </main>

        <footer className="mt-6 text-center text-gray-500 text-sm">
          <p>Available for opportunities • Contact: jon@example.com</p>
        </footer>
      </div>
    </div>
  );
}

export default App;
