import React, { useState, useEffect, useRef } from 'react';

function App() {
  const [currentSection, setCurrentSection] = useState('about');
  const [isLoading, setIsLoading] = useState(true);
  const [bootSequence, setBootSequence] = useState([]);
  const mainRef = useRef(null);

  const scrambleText = (element, finalText, duration = 1000) => {
    const chars = '!<>-_\\/[]{}â€”=+*^?#________';
    let startTime = null;

    const animate = (timestamp) => {
      if (!startTime) startTime = timestamp;
      const progress = (timestamp - startTime) / duration;

      if (progress < 1) {
        const scrambled = finalText
          .split('')
          .map((char, index) => {
            if (progress * finalText.length > index) {
              return char;
            } else {
              return chars[Math.floor(Math.random() * chars.length)];
            }
          })
          .join('');

        element.textContent = scrambled;
        requestAnimationFrame(animate);
      } else {
        element.textContent = finalText;
      }
    };

    requestAnimationFrame(animate);
  };

  const handleTextScramble = (e) => {
    const element = e.target;
    const finalText = element.getAttribute('data-text') || element.textContent;
    scrambleText(element, finalText, 800);
  };

  const sections = {
    about: 'About',
    experience: 'Experience', 
    education: 'Education',
    skills: 'Skills',
    projects: 'Projects',
    certifications: 'Certifications'
  };

  const sectionKeys = Object.keys(sections);

  const bootMessages = [
    'Loading system modules...',
    'Initializing portfolio interface...',
    'Mounting file systems...',
    'Starting network services...',
    'Loading user profile...',
    'Ready.'
  ];

  useEffect(() => {
    let messageIndex = 0;
    const bootInterval = setInterval(() => {
      if (messageIndex < bootMessages.length) {
        setBootSequence(prev => [...prev, bootMessages[messageIndex]]);
        messageIndex++;
      } else {
        clearInterval(bootInterval);
        setTimeout(() => setIsLoading(false), 500);
      }
    }, 300);

    return () => clearInterval(bootInterval);
  }, []);

  useEffect(() => {
    const handleKeyDown = (e) => {
      if (isLoading) return;

      const currentIndex = sectionKeys.indexOf(currentSection);

      if (e.key === 'ArrowLeft' || e.key === 'ArrowUp') {
        e.preventDefault();
        const prevIndex = currentIndex > 0 ? currentIndex - 1 : sectionKeys.length - 1;
        setCurrentSection(sectionKeys[prevIndex]);
      } else if (e.key === 'ArrowRight' || e.key === 'ArrowDown') {
        e.preventDefault();
        const nextIndex = currentIndex < sectionKeys.length - 1 ? currentIndex + 1 : 0;
        setCurrentSection(sectionKeys[nextIndex]);
      } else if (e.key >= '1' && e.key <= '6') {
        e.preventDefault();
        const index = parseInt(e.key) - 1;
        if (index < sectionKeys.length) {
          setCurrentSection(sectionKeys[index]);
        }
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [currentSection, isLoading, sectionKeys]);

  const renderPrompt = () => (
    <div className=" section-role font-medium text-muted">
      <span className="accent">~/</span>
      <span className="text-secondary">{currentSection}</span>
    </div>
  );

  const renderAbout = () => (
    <div className="">
      {renderPrompt()}
      <div className="">
        <div className="leading-relaxed  section-role">
          <p>Final year Computer Engineering student</p>
          <p>Hands-on experience in Linux, AWS, Docker, Python and CI/CD using GitHub Actions. Self-driven, solutions-oriented and committed to continuous improvement, bringing analytical thinking, product vision and clear communication.</p>
          <div className="pt-2">
            <span>Languages:</span> Spanish (native), English (C1), German (A2)
          </div>
        </div>
      </div>
    </div>
  );

  const renderEducation = () => (
    <div className="">
      {renderPrompt()}
      <div className="">
        <div className="">
          <h3 className="section-title">Universitat Oberta de Catalunya â€¢ <span className="section-meta">2026</span></h3>
          <p className="section-role">Bachelor in Computer Science - Computer Engineering</p>
          <div className="section-details  ">
            <p># Network and Operating System Administration</p>
            <p># Internet Networks and Applications</p>
            <p># Computer Architecture</p>
            <p># Graph Theory and Complexity</p>
            <p># Artificial Intelligence</p>
          </div>
        </div>
      </div>
    </div>
  );

  const renderExperience = () => (
    <div className="">
      {renderPrompt()}
      <div className="">
        <div className="">
          <div className="">
            <h3 className="section-title">Default Club (E-commerce) â€¢ <span className="section-meta">2023 - 2025</span></h3>
            <p className="section-role">Cloud Automation Engineer</p>
            <div className="section-details  ">
              <p># Automated product catalogue, saving 20h/week via REST/GraphQL APIs</p>
              <p># Introduced CI/CD with GitHub Actions and Docker on AWS EC2</p>
              <p># Deployed monitoring with Prometheus, KPIs and Discord alerts</p>
              <p># Web scraping and market analysis with Python (BeautifulSoup, Pandas)</p>
              <p># Built cloud pipelines with AWS S3 and Lambda for serverless operations</p>
            </div>
          </div>

          <div className="">
            <h3 className="section-title">Default Club â€¢ <span className="section-meta">2017 - 2025</span></h3>
            <p className="section-role">Founder</p>
            <div className="section-details  ">
              <p># Built and scaled e-commerce brand from scratch, serving 25,000+ customers</p>
              <p># Led technical automation of core operations focusing on performance, scalability, and low operational overhead</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );

  const renderSkills = () => (
    <div className="">
      {renderPrompt()}
      <div className="">
        <div className="">
          <div className="">
            <h4 className="section-title">Programming</h4>
            <p className="section-details">Python | JavaScript | React | Node.js | HTML | SQL | Bash</p>
          </div>

          <div className="">
            <h4 className="section-title">DevOps & Cloud</h4>
            <p className="section-details">Docker | Kubernetes | Terraform | Git | AWS | PostgreSQL | Prometheus</p>
          </div>

          <div className="">
            <h4 className="section-title">Systems</h4>
            <p className="section-details">Linux Administration | CLI Tools | bash | ssh | systemctl | journalctl | curl</p>
          </div>
        </div>
      </div>
    </div>
  );

  const renderProjects = () => (
    <div className="">
      {renderPrompt()}
      <div className="">
        <div className="">
          <h3 className="section-title">Fbref Scraper</h3>
          <p className="section-meta">Docker | Python | Requests | BeautifulSoup | Pandas | GitHub â€¢ <span className="accent">Personal Project</span></p>
        </div>
      </div>
    </div>
  );

  const renderCertifications = () => (
    <div className="">
      {renderPrompt()}
      <div className="">
        <div className="">
          <div className="">
            <h3 className="section-title">DevOps with Docker & Kubernetes</h3>
            <p className="section-details">Universidad de Helsinki (240h) â€¢ <span className="accent">In progress</span></p>
          </div>

          <div className="">
            <h3 className="section-title">Google Professional Certificate in Business Intelligence</h3>
            <p className="section-details">Google (80h) â€¢ <span className="accent">2025</span></p>
          </div>
        </div>
      </div>
    </div>
  );

  const renderContent = () => {
    switch(currentSection) {
      case 'about': return renderAbout();
      case 'education': return renderEducation();
      case 'experience': return renderExperience();
      case 'skills': return renderSkills();
      case 'projects': return renderProjects();
      case 'certifications': return renderCertifications();
      default: return renderAbout();
    }
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-black text-green-400 font-mono p-8">
        <div className="max-w-4xl mx-auto">
          <div className="">
            <span className="accent">jon@portfolio</span>
            <span className="text-muted">:~$ </span>
            <span className="animate-pulse text-secondary">portfolio --init</span>
          </div>
          {bootSequence.map((message, index) => (
            <div key={index} className="section-role opacity-80 mb-1 boot-message">
              <span className="accent">[{String(index + 1).padStart(2, '0')}]</span> <span className="text-secondary">{message}</span>
            </div>
          ))}
          <div className="terminal-cursor"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen font-mono" style={{backgroundColor: 'var(--bg-primary)', color: 'var(--text-primary)'}}>
      <div className="max-w-4xl mx-auto p-6">
        <header className="mb-6">
          <div className="text-center ">
            <h1 className="text-3xl font-medium  text-primary">Jon Hidalgo</h1>
            <p className="section-role text-secondary">Computer Engineering Student â€¢ DevOps & Cloud Automation</p>
          </div>

          <nav className="nav-minimal p-4" role="tablist">
            <div className="flex flex-wrap gap-3 justify-center">
              {Object.entries(sections).map(([key, label], index) => (
                <button
                  key={key}
                  onClick={() => setCurrentSection(key)}
                  className={`nav-button px-4 py-2 section-role font-medium ${
                    currentSection === key ? 'active' : ''
                  }`}
                  aria-label={`Navigate to ${label} section. Press ${index + 1} or use arrow keys`}
                  role="tab"
                  aria-selected={currentSection === key}
                  tabIndex={currentSection === key ? 0 : -1}
                >
                  <span className="text-xs opacity-60 mr-2">{index + 1}</span>
                  {label}
                </button>
              ))}
            </div>
            <div className="text-xs text-center mt-4 text-muted">
              <span className="sr-only">Navigation instructions: </span>
              Use arrow keys or numbers 1-6 to navigate
            </div>
          </nav>
        </header>

        <main 
          ref={mainRef}
          className="card-minimal p-6 min-h-96"
          role="tabpanel"
          aria-labelledby="current-section"
          tabIndex="0"
        >
          <div id="current-section" className="sr-only">
            {sections[currentSection]} section content
          </div>
          {renderContent()}
        </main>

        <footer className="mt-4 text-center text-xs">
          <p className="text-muted">Available for opportunities â€¢ <span className="accent hyperlink text-scramble" data-text="contact@jonhidalgo.dev" onMouseEnter={handleTextScramble}>contact@jonhidalgo.dev</span></p>
        </footer>
      </div>
    </div>
  );
}

export default App;
