import React from 'react';

const Hero = () => {
  // Scroll to contact section when button is clicked
  const scrollToContact = () => {
    const contactSection = document.getElementById('contact');
    if (contactSection) {
      contactSection.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <section id="hero" className="min-h-screen flex items-center justify-center bg-gradient-to-br from-gray-50 to-gray-100 pt-16">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        <div className="text-center">
          {/* Main heading with name */}
          <h1 className="text-4xl sm:text-5xl md:text-6xl font-bold text-gray-900 mb-6">
            Hi, I'm{' '}
            <span className="text-primary-600 bg-gradient-to-r from-primary-600 to-primary-700 bg-clip-text text-transparent">
              Jon Hidalgo
            </span>
          </h1>

          {/* Subtitle/Introduction */}
          <div className="max-w-3xl mx-auto mb-8">
            <p className="text-xl sm:text-2xl text-gray-600 mb-4">
              Aspiring Cloud Engineer & DevOps Enthusiast
            </p>
            <p className="text-lg text-gray-500 leading-relaxed">
              Computer Science student passionate about cloud infrastructure, automation, and scalable systems. 
              I focus on building robust, automated solutions using modern DevOps practices and cloud technologies 
              to deliver reliable, efficient, and maintainable infrastructure.
            </p>
          </div>

          {/* Skills highlight */}
          <div className="mb-8">
            <p className="text-gray-600 mb-4">Specialized in</p>
            <div className="flex flex-wrap justify-center gap-3 max-w-2xl mx-auto">
              {['AWS', 'Docker', 'Terraform', 'GitHub Actions', 'Python', 'Linux'].map((skill) => (
                <span
                  key={skill}
                  className="px-4 py-2 bg-white text-gray-700 rounded-full shadow-sm border border-gray-200 text-sm font-medium"
                >
                  {skill}
                </span>
              ))}
            </div>
          </div>

          {/* Contact button */}
          <div className="flex flex-col sm:flex-row gap-4 justify-center items-center">
            <button
              onClick={scrollToContact}
              className="bg-primary-600 hover:bg-primary-700 text-white font-semibold py-3 px-8 rounded-lg shadow-lg hover:shadow-xl transform hover:-translate-y-1 transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:ring-offset-2"
            >
              Get In Touch
            </button>
            <a
              href="#projects"
              className="text-primary-600 hover:text-primary-700 font-semibold py-3 px-8 rounded-lg border-2 border-primary-600 hover:border-primary-700 transition-colors duration-200 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:ring-offset-2"
            >
              View My Work
            </a>
          </div>

          {/* Scroll indicator */}
          <div className="mt-16 animate-bounce">
            <a href="#about" className="text-gray-400 hover:text-gray-600 transition-colors duration-200">
              <svg
                className="w-6 h-6 mx-auto"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="2"
                  d="M19 14l-7 7m0 0l-7-7m7 7V3"
                />
              </svg>
            </a>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
