import React from 'react';

const Skills = () => {
  // Skill proficiency levels
  const skillLevels = {
    BEGINNER: { name: 'Beginner', level: 1, color: 'bg-yellow-400' },
    INTERMEDIATE: { name: 'Intermediate', level: 2, color: 'bg-blue-400' },
    ADVANCED: { name: 'Advanced', level: 3, color: 'bg-green-400' }
  };

  // Organized skill groups focused on Cloud/DevOps with proficiency levels (ordered by proficiency)
  const skillGroups = [
    {
      title: 'Cloud & DevOps',
      icon: 'â˜ï¸',
      skills: [
        { name: 'Linux', level: skillLevels.ADVANCED },
        { name: 'Bash', level: skillLevels.ADVANCED },
        { name: 'AWS', level: skillLevels.INTERMEDIATE },
        { name: 'Docker', level: skillLevels.INTERMEDIATE },
        { name: 'GitHub Actions', level: skillLevels.INTERMEDIATE },
        { name: 'Terraform', level: skillLevels.BEGINNER }
      ]
    },
    {
      title: 'Languages',
      icon: 'ðŸ’»',
      skills: [
        { name: 'Python', level: skillLevels.ADVANCED },
        { name: 'JavaScript', level: skillLevels.INTERMEDIATE },
        { name: 'TypeScript', level: skillLevels.INTERMEDIATE },
        { name: 'SQL', level: skillLevels.INTERMEDIATE }
      ]
    },
    {
      title: 'Tools',
      icon: 'ðŸ› ï¸',
      skills: [
        { name: 'Trello', level: skillLevels.ADVANCED },
        { name: 'Postman', level: skillLevels.INTERMEDIATE },
        { name: 'APIs (REST & GraphQL)', level: skillLevels.INTERMEDIATE },
        { name: 'Jira', level: skillLevels.INTERMEDIATE }
      ]
    },
    {
      title: 'Frameworks/Libraries',
      icon: 'ðŸ“š',
      skills: [
        { name: 'pandas', level: skillLevels.ADVANCED },
        { name: 'NumPy', level: skillLevels.ADVANCED },
        { name: 'React.js', level: skillLevels.INTERMEDIATE },
        { name: 'Node.js', level: skillLevels.INTERMEDIATE },
        { name: 'scikit-learn', level: skillLevels.INTERMEDIATE },
        { name: 'PostgreSQL', level: skillLevels.INTERMEDIATE },
        { name: 'MongoDB', level: skillLevels.BEGINNER }
      ]
    }
  ];

  // Component to render WiFi-style skill level indicator
  const SkillLevelIndicator = ({ level }) => {
    const maxLevel = 3;
    return (
      <div className="flex items-end space-x-1">
        {[...Array(maxLevel)].map((_, index) => {
          const isActive = index < level.level;
          const height = `${(index + 1) * 4 + 8}px`; // 12px, 16px, 20px
          return (
            <div
              key={index}
              className={`w-1 rounded-t ${
                isActive ? level.color : 'bg-gray-300'
              }`}
              style={{ height }}
            />
          );
        })}
      </div>
    );
  };

  return (
    <section id="skills" className="py-20 bg-white">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Section header */}
        <div className="text-center mb-16">
          <h2 className="text-3xl sm:text-4xl font-bold text-gray-900 mb-4">
            Technical Skills
          </h2>
          <p className="text-lg text-gray-600 max-w-2xl mx-auto">
            Core technologies and tools I use to build scalable cloud infrastructure, 
            automate deployments, and manage modern DevOps pipelines.
          </p>
        </div>

        {/* Skills grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
          {skillGroups.map((group, groupIndex) => (
            <div
              key={group.title}
              className="bg-gray-50 rounded-xl p-6 hover:shadow-lg transition-shadow duration-300"
            >
              {/* Group header */}
              <div className="flex items-center mb-6">
                <span className="text-2xl mr-3">{group.icon}</span>
                <h3 className="text-xl font-semibold text-gray-900">
                  {group.title}
                </h3>
              </div>

              {/* Skills list */}
              <div className="space-y-3">
                {group.skills.map((skill, skillIndex) => (
                  <div
                    key={skill.name}
                    className="flex items-center justify-between p-3 bg-white rounded-lg shadow-sm hover:shadow-md transition-shadow duration-200"
                    style={{
                      animationDelay: `${(groupIndex * 3 + skillIndex) * 0.1}s`
                    }}
                  >
                    <div className="flex items-center flex-1">
                      {/* Skill name */}
                      <span className="text-gray-700 font-medium">{skill.name}</span>
                    </div>

                    <div className="flex items-center space-x-2">
                      {/* Skill level indicator */}
                      <SkillLevelIndicator level={skill.level} />

                      {/* Level badge */}
                      <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                        skill.level.level === 3 
                          ? 'bg-green-100 text-green-800' 
                          : skill.level.level === 2 
                          ? 'bg-blue-100 text-blue-800' 
                          : 'bg-yellow-100 text-yellow-800'
                      }`}>
                        {skill.level.name}
                      </span>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>

        {/* Skill level legend */}
        <div className="mt-12 max-w-md mx-auto">
          <h3 className="text-lg font-semibold text-gray-900 mb-4 text-center">
            Proficiency Levels
          </h3>
          <div className="flex justify-center space-x-6">
            {Object.values(skillLevels).map((level) => (
              <div key={level.name} className="flex items-center space-x-2">
                <div className="flex items-end space-x-1">
                  {[...Array(3)].map((_, index) => {
                    const isActive = index < level.level;
                    const height = `${(index + 1) * 3 + 6}px`; // 9px, 12px, 15px
                    return (
                      <div
                        key={index}
                        className={`w-1 rounded-t ${
                          isActive ? level.color : 'bg-gray-300'
                        }`}
                        style={{ height }}
                      />
                    );
                  })}
                </div>
              </div> 
            ))}
          </div>
        </div>

        {/* Additional info section */}
        <div className="mt-16 text-center">
          <div className="bg-gradient-to-r from-primary-50 to-blue-50 rounded-xl p-8">
            <h3 className="text-xl font-semibold text-gray-900 mb-4">
              Always Learning
            </h3>
            <p className="text-gray-600 max-w-3xl mx-auto">
              Cloud technology evolves rapidly, and I'm committed to continuous learning. 
              Currently pursuing AWS certifications and exploring advanced DevOps practices like 
              Infrastructure as Code, CI/CD optimization, and cloud-native architectures.
            </p>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Skills;
